from hawc.apps.common.dsstox import fetch_dsstox


def test_dsstox():
    # bad casrn failure
    response = fetch_dsstox("abc-123")
    assert response == {}

    # test success
    response = fetch_dsstox("1836-75-5")
    assert response == {
        "CommonName": "Nitrofen",
        "SMILES": "[O-][N+](=O)C1=CC=C(OC2=C(Cl)C=C(Cl)C=C2)C=C1",
        "MW": 284.09,
        "DTXSID": "DTXSID7020970",
        "image": "",  # noqa
        "status": "success",
    }
