from hawc.apps.common.chemspider import fetch_chemspider


def test_chemspider():
    # test success
    response = fetch_chemspider("1836-75-5")
    assert response == {
        "CommonName": "nitrofen",
        "SMILES": "c1cc(ccc1[N+](=O)[O-])Oc2ccc(cc2Cl)Cl",
        "MW": 284.0949,
        "image": "",  # noqa
        "status": "success",
    }

    # bad casrn failure
    response = fetch_chemspider("abc-123")
    assert response == {}
